import { mapGetters, mapMutations, mapActions } from 'vuex';
import Swal from 'sweetalert2';
export default {
  computed: {
    ...mapGetters(['cartItems', 'totalPrice'])
  },
  methods: {
    ...mapMutations(['removeFromCart', 'incrementQuantity', 'decrementQuantity', 'clearCart']),
    ...mapActions(['purchaseAll']),
    async confirmPurchaseAll() {
      const result = await Swal.fire({
        title: 'Are you sure?',
        text: 'Do you want to purchase all items in your cart?',
        icon: 'question',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Yes, purchase all!'
      });
      if (result.isConfirmed) {
        await this.purchaseAll();
        Swal.fire('Purchased!', 'All items have been purchased.', 'success');
        this.clearCart(); // Call the clearCart method after the purchase is confirmed
      }
    },
    clearCart() {
      // Clear the cart items
      this.$store.commit('clearCart');
    },
    increaseQuantity(index) {
      this.incrementQuantity(index);
      this.calculateTotalPrice();
    },
    decrementQuantity(index) {
      if (this.cartItems[index].quantity > 1) {
        this.$store.commit('decrementQuantity', index);
        this.calculateTotalPrice();
      } else {
        this.confirmRemove(index);
      }
    },
    confirmRemove(index) {
      Swal.fire({
        title: 'Are you sure?',
        text: 'Do you want to remove this item from your cart?',
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#d33',
        cancelButtonColor: '#3085d6',
        confirmButtonText: 'Yes, remove it!'
      }).then(result => {
        if (result.isConfirmed) {
          this.removeFromCart(index);
          Swal.fire('Removed!', 'Your item has been removed from the cart.', 'success');
        }
      });
    },
    calculateTotalPrice() {
      // Calculate total price
    }
  }
};
