// store/index.js
import { createStore } from 'vuex'

export default createStore({
  state: {
    books: [
      { id: 1, title: 'Book 1', price: 10 },
      { id: 2, title: 'Book 2', price: 15 },
      // Add more books here
    ],
    cart: [],
    isAuthenticated: false
  },
  mutations: {
    addToCart(state, book) {
      state.cart.push(book)
    },
    removeFromCart(state, index) {
      state.cart.splice(index, 1)
    },
    updateAuthenticationStatus(state, status) {
      state.isAuthenticated = status
    }
  },
  actions: {
    purchase({ state }) {
      // Handle purchase transactions asynchronously
      // Example: Here you can simulate a purchase by updating inventory, deducting amount from user's account, etc.
    },
    authenticate({ commit }, credentials) {
      // Simulate user authentication and authorization
      // Example: You can authenticate against a backend API
      const isAuthenticated = credentials.username === 'user' && credentials.password === 'password'
      commit('updateAuthenticationStatus', isAuthenticated)
    }
  },
  getters: {
    availableBooks: state => state.books,
    cartItems: state => state.cart,
    totalPrice: state => state.cart.reduce((total, item) => total + item.price, 0)
  }
})
